CodeBlockMethod.Code:
local sec = math.floor(self.playTime)
---------------
if sec < 10 then self.timeText.Text = "00:0"..tostring(sec)
elseif sec < 60 then self.timeText.Text = "00:"..tostring(sec)
elseif sec < 600 then local min = math.floor(sec / 60) local csec = sec % 60
if csec < 10 then self.timeText.Text = "0"..tostring(min)..":0"..tostring(csec)
elseif csec < 60 then self.timeText.Text = "0"..tostring(min)..":"..tostring(csec) end
else local min = math.floor(sec / 60) local csec = sec % 60
if csec < 10 then self.timeText.Text = "0"..tostring(min)..":0"..tostring(csec)
elseif csec < 60 then self.timeText.Text = "0"..tostring(min)..":"..tostring(csec) end end
